import React, { Component } from 'react';
import './trailer.css';
import sample from './dummy.mp4';
import sampleweb from './dummy.webm';

class trailer extends Component{
    constructor(props) {
        super(props);

        const vdo = this.refs.vdo;
    }

    componentDidMount() {
        let width = window.innerWidth;
        if(width > 768) {
            this.vdo.pause;
        }
    }

    render(){
        return(
            <section className="trailer-wrapper">
                <div className="overlay-video">
                    <h2>FIGHT TEAM</h2>
                </div>
                <video ref ='vdo' className="trailer" playsinline autoPlay loop muted>
                    <source className="video-display" src={sample} type="video/mp4" />
                    <source className="video-mobile" src={sampleweb} type="video/webm" />
                </video>
            </section>
        );
    }

}

export default trailer;
