import React, { Component } from  'react';
import Element from './element.js';
import Square from './square';

import './follow.css';

class Carousel extends Component {

    constructor(props) {
        super(props);
        this.state= {
            data: Element.properties,
            current: Element.properties[0]
        }
    }

    /* Next and Back Button Actions*/ 
    nextButton = () => {
        const next = this.state.property.index + 1;
        if(next < this.data.size) {
            this.setState({
                current: Element.properties[next]
            })
        }
    }

    backButton = () => {
        const next = this.state.property.index - 1;
        if(next > -1) {
            this.setState({
                current: Element.properties[next]
            })
        }
    }

    /* Official Render */
    render(){
        return(
            <section className="social-container">
                <div className="follow">
                    <h2>@TWINTIGERSRENTON</h2>
                    <h1>FOLLOW MMA</h1>
                </div>

                <div className='carousel-container'>
                    Elements.map
                </div>

                <div className="follow">
                    <h2>@TWINTIGERSRENTON</h2>
                    <h1>SOCIAL MEDIA</h1>
                </div>
            </section>
        );
    }
}

export default Carousel;
