import React, { Component } from  'react';
import Element from './element.js';
import Square from './square';

import './follow.css';

class Carousel extends Component {

    constructor(props) {
        super(props);
        this.state = {
            properties: Element.properties,
            property: Element.properties[3]
        }
    }

    next= () => {
        const newIndex = this.state.property.index+1;
        this.setState({
          property: Element.properties[newIndex]
        })
      }
    
      prev = () => {
        const newIndex = this.state.property.index-1;
        this.setState({
          property: Element.properties[newIndex]
        })
      }
    
    render(){
        const {properties, property} = this.state;
        return(
            <section className="social-container">
                <div className="follow">
                    <h2>@TWINTIGERSRENTON</h2>
                    <h1>FOLLOW MMA</h1>
                </div>

                <div className="carousel-container">
                        <div className="carousel-buttons">
                            <button onClick={() => this.prev()} disabled={property.index === 0}>Prev</button>
                            <div className="separation"></div>
                            <button onClick={() => this.next()} disabled={property.index === Element.properties.length-1}>Next</button>
                        </div>
                    
                        <div className="wrapper" style={{
                            'transform': `translateX(-${10/property.index*(properties.length)}%)`
                        }}>

                        {
                            properties.map(property => <Square key={property.id} property={property} />)
                        }
                        </div>
                </div>            
                <div className="follow">
                    <h2>@TWINTIGERSRENTON</h2>
                    <h1>SOCIAL MEDIA</h1>
                </div>
            </section>
        );
    }
}

export default Carousel;
