import React, { Component } from 'react';
import './navbar.css';

class Nav extends Component {

    constructor(){
        super();
        this.state = {
            scrolled: false,
            side: false,
        }
    }

    componentDidMount(){
        window.addEventListener('scroll', () => {
            const isTop = window.scrollY < 100;
            if(isTop !== true) {
                this.setState({scrolled: true});
            } else {
                this.setState({scrolled: false});
            }
        });
    }

    componentWillUnmount() {
        window.removeEventListener('scroll');
    }

    render(){
        return(
            <header className={this.state.scrolled ? 'translucent-scrolled' : 'translucent'}>
                <div className="hamburger"
                onClick={ () => this.setState({side: !this.side})}>
                    <i class="fa fa-bars" aria-hidden="true"></i>
                </div>
                
                <div className={ this.state.side ? "menu-active" : "menu"}>
                    
                    {/*Exit*/}
                    <i class="fa fa-times" aria-hidden="true"></i>
                    
                    <a href='/About'>ABOUT</a>
                    <div className="menu-line"></div>

                    <a href='/Classes'>CLASSES</a>
                    <div className="menu-line"></div>
                    
                    <a href='/Payment'>PAYMENT</a>
                    <div className="menu-line"></div>
                    
                    <a href='/Rules'>RULES</a>
                    <div className="menu-line"></div>

                    <a href='/Register'>REGISTRATION</a>
                    <div className="menu-line"></div>

                    <a href='/Events'>EVENTS</a>
                    <div className="menu-line"></div>

                </div>

                <ul>
                    <li><a href='/About'>ABOUT</a></li>
                    <li><a href='/Classes'>CLASSES</a></li>
                    <li><a href='/Payment'>PAYMENT</a></li>
                </ul>
                <a className='logo-container' href='/'></a>
                <ul>
                    <li><a href='/Rules'>RULES</a></li>
                    <li><a href='/Register'>REGISTRATION</a></li>
                    <li><a href='/Events'>EVENTS</a></li>
                </ul>

                <div className="filler-other"></div>
            </header>
        );
    }

}

export default Nav;
