import React, { Component } from 'react';
import './navbar.css';

class Nav extends Component {
    constructor(props){
        super(props);
        this.nav = React.createRef(this.refs.nav);
    }


    scroll() {
        const navcurrent = this.refs.nav;
        const sticky = this.nav.offsetTop();

        if(window.pageYOffset >=  sticky) {
            navcurrent.classList.add("sticky");
        } else {
            navcurrent.classList.remove("sticky");
        }
    }

    render(){
        window.onscroll= this.scroll();
        return(
            <header className='translucent' refs='nav'>
                <ul>
                    <li><a href='/About'>ABOUT</a></li>
                    <li><a href='#'>CLASSES</a></li>
                    <li><a href='#'>PAYMENT</a></li>
                </ul>
                <a className='logo-container' href='/'></a>
                <ul>
                    <li><a href='/Rules'>RULES</a></li>
                    <li><a href='/Register'>REGISTRATION</a></li>
                    <li><a href='/Events'>EVENTS</a></li>
                </ul>
            </header>
        );
    }

}

export default Nav;
